package com.sparetimedevs.ami.mediaprocessor.util

import cats.data.EitherT
import cats.effect.IO
import cats.effect.unsafe.IORuntime
import com.sparetimedevs.ami.test.data.{bytes1, bytes2}
import com.sparetimedevs.test.util.getRightResultForTest
import org.scalatest.flatspec.AnyFlatSpec
import org.scalatest.matchers.should.Matchers

class Base64sToBytesTest extends AnyFlatSpec with Matchers {

  private given runtime: IORuntime = cats.effect.unsafe.IORuntime.global

  it should "return bytes for parts" in {
    val base64EncodedImagesForParts: Map[String, String] = Map(
      (
        "a",
        ""
      ),
      (
        "b",
        ""
      )
    )

    val result: Map[String, Array[Byte]] = base64sToBytes(base64EncodedImagesForParts).getRightResultForTest

    result.size should be(2)
    result.get("a").orNull should be(bytes1)
    result.get("b").orNull should be(bytes2)
  }
}
